import React, { useState } from "react";
import "./WhatsAppButton.css";

const FloatingButtons = () => {
  const [isCallPopupVisible, setCallPopupVisible] = useState(true);

  const handleCallPopupClose = () => {
    setCallPopupVisible(false);
  };

  const handleCallPopupOpen = () => {
    setCallPopupVisible(true);
  };

  return (
    <div>
      {/* WhatsApp Floating Button */}
      <div className="floating_whatsapp_btn">
        <a
          target="_blank"
          rel="noopener noreferrer"
          href="https://wa.me/8867739948"
        >
          <div className="contact_icon">
            <i className="fab fa-whatsapp"></i>
          </div>
        </a>
        <p className="text_icon">Chat on WhatsApp</p>
      </div>

      {/* Call Button */}
      <div className="floating_call_btn" onClick={handleCallPopupOpen}>
        <div className="call_icon">
          <i className="fas fa-phone-alt"></i>
        </div>
        <p className="text_icon">Need to talk?</p>
      </div>

      {/* Call Popup */}
      {isCallPopupVisible && (
        <div className="call_popup">
          <div className="popup_header">
            <h4>Start a Call</h4>
            <button className="close_btn" onClick={handleCallPopupClose}>
              X
            </button>
          </div>
          <div className="popup_content">
            <p>Click below to initiate a call with support!</p>
            <a href="tel:+1234567890" className="call_now_btn">
              Call Now
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default FloatingButtons;
