import React from 'react';
// import Header from './Header';
// import Footer from './Footer';
// import WhatsAppButton from './WhatsAppButton/WhatsAppButton';

import { Card, Button } from 'react-bootstrap';
import Carousel from "react-bootstrap/Carousel"; // Import Carousel
// import Map from './Map';
import './HeroSection.css';
// import Map from './Map';


// Testimonials Data Starts
const testimonialsData = [
  {
    quote: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Nam, adipisci facilis!",
    author: "Author Name 1",
  },
  {
    quote: "Nam minima sint voluptas eveniet delectus illum libero odit rerum unde.",
    author: "Author Name 2",
  },
  {
    quote: "Culpa enim architecto dolorem accusamus magni.",
    author: "Author Name 3",
  },
  {
    quote: "Culpa enim architecto dolorem accusamus magni.",
    author: "Author Name 3",
  },
  {
    quote: "Culpa enim architecto dolorem accusamus magni.",
    author: "Author Name 3",
  },
  {
    quote: "Culpa enim architecto dolorem accusamus magni.",
    author: "Author Name 3",
  },
  {
    quote: "Culpa enim architecto dolorem accusamus magni.",
    author: "Author Name 3",
  },
  {
    quote: "Culpa enim architecto dolorem accusamus magni.",
    author: "Author Name 3",
  },
];
// Testimonials data ends
const HeroSection = () => {
  return (
    <div>




<div className="responsive-container">
      {/* Left Half */}
      <div className="left-half">
        Industry Leaders <br /> Hire From Us
      </div>
  <div className="right-half">
    <p>
      Join our placement-linked programs and become ‘day one job-ready’ professionals
    </p>
    <div className="cta-container">
      <button>Limited Seats, Join Us Today</button>

    </div>
  </div>
</div>
      {/* Right Half */}


{/* Working starts */}



<section className="testimonials-section">
      <div className="testimonials-container">
        <div className="testimonials-quotes">
          {testimonialsData.map((testimonial, index) => (
            <div key={index} className="quote-wrapper">
              <div className="quote">
                <p>{testimonial.quote}</p>
              </div>
              <div className="author">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="97.891"
                  height="15.578"
                  viewBox="0 0 97.891 15.578"
                  className="star-svg"
                >
                  {/* SVG paths */}
                  <path
                    d="M16.213,6.382a.863.863,0,0,0-.743-.593l-4.693-.426L8.923,1.021a.864.864,0,0,0-1.59,0L5.479,5.363.785,5.789A.865.865,0,0,0,.294,7.3l3.547,3.11L2.8,15.018a.864.864,0,0,0,1.286.934l4.046-2.419,4.046,2.419a.865.865,0,0,0,1.286-.934l-1.046-4.607L15.961,7.3A.865.865,0,0,0,16.213,6.382Zm-8.008,7.1"
                    fill="#ffba00"
                  />
                </svg>
                <div className="author-name">
                  <p>{testimonial.author}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>



{/* Working Ends */}

{/* Testimonials Sections Ends Here */}
{/* Map Is here */}
{/* Footer Is here  */}
    </div>
  );
};

export default HeroSection;
