import React from 'react';
import { Card, Button, Row, Col } from 'react-bootstrap';
import './Update.css';

const Update = [
  {
    id: 1,
    title: "Java Full-Stack Development",
    description: "Learn Java with full-stack development, including backend technologies and frontend frameworks.",
    batchTime: "Mon-Fri, 10:00 AM - 1:00 PM",
    instructor: "John Doe",
    image: "https://via.placeholder.com/150",
  },
  {
    id: 2,
    title: "Python Data Science",
    description: "Master Python and Data Science concepts like Machine Learning, AI, and Data Analysis.",
    batchTime: "Mon-Fri, 2:00 PM - 5:00 PM",
    instructor: "Jane Smith",
    image: "https://via.placeholder.com/150",
  },
  {
    id: 3,
    title: "Web Development (HTML, CSS, JS)",
    description: "Build interactive websites and learn the fundamentals of front-end web development.",
    batchTime: "Sat-Sun, 11:00 AM - 2:00 PM",
    instructor: "Alice Johnson",
    image: "https://via.placeholder.com/150",
  },
  {
    id: 4,
    title: "Cloud Computing with AWS",
    description: "Learn AWS services, deployment, and how to architect scalable cloud solutions.",
    batchTime: "Mon-Wed, 6:00 PM - 8:00 PM",
    instructor: "David Williams",
    image: "https://via.placeholder.com/150",
  },
];

const OngoingClasses = () => {
  return (
    <div className="classes-container">
      <h1 className="page-title">Ongoing Classes</h1>

      <Row className="classes-row">
        {Update.map((classItem) => (
          <Col key={classItem.id} xs={12} sm={6} md={4} lg={3}>
            <Card className="class-card">
              <Card.Img variant="top" src={classItem.image} />
              <Card.Body>
                <Card.Title>{classItem.title}</Card.Title>
                <Card.Text>{classItem.description}</Card.Text>
                <p><strong>Batch Timing:</strong> {classItem.batchTime}</p>
                <p><strong>Instructor:</strong> {classItem.instructor}</p>
                <Button variant="primary">Join Now</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Update;
