import React, { useState } from "react";
import "./CallButton.css";

const CallButton = () => {
  const [isPopupVisible, setPopupVisible] = useState(true);

  const handleButtonClick = () => {
    setPopupVisible(true);
  };

  const handleClosePopup = () => {
    setPopupVisible(false);
  };

  return (
    <div>
      {/* Floating Button */}
      <div className="floating_call_btn" onClick={handleButtonClick}>
        <div className="call_icon">
          <i className="fas fa-phone-alt"></i>
        </div>
        <p className="text_icon">Need to talk?</p>
      </div>

      {/* Call Popup */}
      {isPopupVisible && (
        <div className="call_popup">
          <div className="popup_header">
            <h4>Start a Call</h4>
            <button className="close_btn" onClick={handleClosePopup}>
              X
            </button>
          </div>
          <div className="popup_content">
            <p>Click below to initiate a call with support!</p>
            <a href="tel:+1234567890" className="call_now_btn">
              Call Now
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

export default CallButton;
