import React, { useState } from 'react';
import { Card, Button } from 'react-bootstrap';
import Carousel from "react-bootstrap/Carousel"; // Import Carousel
import './HeroSection.css';

// Testimonials Data Starts
const testimonials = [
  {
    quote: '"These treats are real tail-waggers"',
    text: "My pug Sir Biscuit is obsessed with the Salmon Snackers! He does a little dance when he hears the Barkin' Baxter's truck coming.",
    author: 'Lily R.'
  },
  {
    quote: '"A bona-fido game-changer"',
    text: "Baxters are at the top of the food chain! Maximus is a picky eater but he loves these treats.",
    author: 'Albert Z.'
  },
  {
    quote: '"Treat time has never been so dog-gone amazing"',
    text: "Our little Mabel gives them two paws up and a whole lot of slobbery kisses.",
    author: 'Jennifer T.'
  }
];

// Testimonials data ends

const HeroSection = () => {
  const [slideIn, setSlideIn] = useState(false);

  const toggleSlide = () => {
    setSlideIn(!slideIn);
  };

  return (
    <div>
      <div className="responsive-container">
        {/* Left Half */}
        <div className="left-half">
          Industry Leaders <br /> Hire From Us
        </div>
        {/* Right Half */}
        <div className="right-half">
          <p>
            Join our placement-linked programs and become ‘day one job-ready’ professionals
          </p>
          <div className="cta-container">
            <button>Limited Seats, Join Us Today</button>
          </div>
        </div>
      </div>

      {/* Testimonials Section */}
      <div className={`body ${slideIn ? 'slide-in' : ''}`}>
        <section className="testimonials-container">
          <h2>What Our Fans Are Saying</h2>
          <div className="testimonials">
            {testimonials.map((testimonial, index) => (
              <div className="testimonial" key={index}>
                <h3>{testimonial.quote}</h3>
                <p>{testimonial.text} <span className="testimonial-author">{testimonial.author}</span></p>
              </div>
            ))}
          </div>
        </section>
      </div>
      
      {/* Toggle Button */}
      <button className="toggle-button" onClick={toggleSlide}>
        Toggle Slide
      </button>
      
      {/* Footer */}
      {/* Footer Is here  */}
    </div>
  );
};

export default HeroSection;
