import React, { useState } from 'react';
import { Card, Button } from 'react-bootstrap';
// import Carousel from "react-bootstrap/Carousel"; // Import Carousel
import './HeroSection.css';
import OwlCarousel from 'react-owl-carousel';


// Testimonials Data Starts
const testimonials = [
  {
    image: "https://images.pexels.com/photos/704955/pexels-photo-704955.jpeg?w=940&h=650&auto=compress&cs=tinysrgb",
    description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed malesuada vulputate nisi in fermentum. Vivamus ac libero quis nisi auctor pulvinar. Aenean sit amet lectus posuere, mattis massa eget, ullamcorper diam. Nunc sit amet felis eget arcu congue dictum.",
    name: "Jessica",
    post: "Architect"
  },
  {
    image: "https://images.pexels.com/photos/638700/pexels-photo-638700.jpeg?w=940&h=650&auto=compress&cs=tinysrgb",
    description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed malesuada vulputate nisi in fermentum. Vivamus ac libero quis nisi auctor pulvinar. Aenean sit amet lectus posuere, mattis massa eget, ullamcorper diam. Nunc sit amet felis eget arcu congue dictum.",
    name: "Pearl",
    post: "Engineer"
  },
  {
    image: "https://images.pexels.com/photos/531139/pexels-photo-531139.jpeg?w=940&h=650&auto=compress&cs=tinysrgb",
    description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed malesuada vulputate nisi in fermentum. Vivamus ac libero quis nisi auctor pulvinar. Aenean sit amet lectus posuere, mattis massa eget, ullamcorper diam. Nunc sit amet felis eget arcu congue dictum.",
    name: "Kellie",
    post: "Graphic Designer"
  }
];

// Testimonials data ends

const HeroSection = () => {


  return (
    <div>
      <div className="responsive-container">
        {/* Left Half */}
        <div className="left-half">
          Industry Leaders <br /> Hire From Us
        </div>
        {/* Right Half */}
        <div className="right-half">
          <p>
            Join our placement-linked programs and become ‘day one job-ready’ professionals
          </p>
          <div className="cta-container">
            <button>Limited Seats, Join Us Today</button>
          </div>
        </div>
      </div>

      {/* Testimonials Section */}

      
    <div className="container">
      <div className="row">
        <div className="col-md-12">
          <OwlCarousel items={2} itemsDesktop={[1000, 2]} itemsDesktopSmall={[990, 2]} itemsTablet={[768, 1]} pagination={true} navigation={false} autoPlay={true}>
            {testimonials.map((testimonial, index) => (
              <div className="testimonial" key={index}>
                <div className="pic">
                  <img src={testimonial.image} alt={testimonial.name} />
                </div>
                <p className="description">{testimonial.description}</p>
                <h3 className="title">{testimonial.name}</h3>
                <small className="post">- {testimonial.post}</small>
              </div>
            ))}
          </OwlCarousel>
        </div>
      </div>
    </div>

      
      {/* Footer */}
      {/* Footer Is here  */}
    </div>
  );
};

export default HeroSection;
