import React, { useState } from 'react';
import './Update.css';
import Header from '../components/Header';
import Footer from '../components/Footer';

const Update = () => {
  const [showTable, setShowTable] = useState(false);
  const [schedule, setSchedule] = useState([]);

  const handleShowSchedule = (className) => {
    let scheduleData = [];

    switch (className) {
      case 'Java Programming':
        scheduleData = [
          { time: '10:30 to 11:30', activity: 'Shweta Core Java' },
          { time: '12:00 to 13:00', activity: 'Project Review' },
          { time: '13:00 to 14:00', activity: 'Lunch Break' },
          { time: '14:00 to 17:00', activity: 'Shivraj (Spring)' },
          { time: '17:00 to 18:00', activity: 'Project Review' },
          { time: '18:00 to 20:00', activity: 'Spring Project' },
        ];
        break;
      case 'Python Programming':
        scheduleData = [
          { time: '10:00 to 12:00', activity: 'Introduction to Python' },
          { time: '12:30 to 14:00', activity: 'Data Structures in Python' },
          { time: '14:00 to 15:30', activity: 'Lunch Break' },
          { time: '15:30 to 17:30', activity: 'Project Work' },
        ];
        break;
      case 'Web Development':
        scheduleData = [
          { time: '09:00 to 10:30', activity: 'HTML Basics' },
          { time: '10:45 to 12:15', activity: 'CSS Advanced' },
          { time: '12:30 to 14:00', activity: 'Lunch Break' },
          { time: '14:15 to 16:00', activity: 'JavaScript Essentials' },
        ];
        break;
      default:
        scheduleData = [];
    }

    setSchedule(scheduleData);
    setShowTable(true);
  };

  const handleCloseTable = () => {
    setShowTable(false);
    setSchedule([]);
  };

  return (
    <>
      <Header />
      <div>
        
      </div>
      <Footer />
    </>
  );
};

export default Update;
