import React from 'react';
import { Carousel, Button } from 'react-bootstrap';
import './HeroSection.css';

const testimonials = [
  {
    text: "Habitasse lobortis cum malesuada nullam cras odio venenatis nisl at turpis sem in porta consequat massa a mus massa nascetur elit vestibulum a.",
    name: "Jane Doe",
    role: "Web Developer",
    image: "https://randomuser.me/api/portraits/women/21.jpg",
  },
  {
    text: "Aenean a neque ipsum. In viverra mauris nibh, nec dapibus nibh imperdiet at. Nulla urna odio, aliquam tincidunt posuere quis, placerat nec sem.",
    name: "Jane Doe",
    role: "Web Developer",
    image: "https://randomuser.me/api/portraits/women/65.jpg",
  },
  {
    text: "Donec in quam eget arcu convallis maximus. In hac habitasse platea dictumst. Etiam commodo faucibus vestibulum. Aliquam vitae consectetur velit.",
    name: "Jane Doe",
    role: "Web Developer",
    image: "https://randomuser.me/api/portraits/women/96.jpg",
  },
  {
    text: "Phasellus malesuada arcu sed justo dapibus bibendum. Mauris cursus, dui sit amet vehicula fermentum, mauris ante lobortis mi, eget accumsan nibh est ut lorem.",
    name: "Jane Doe",
    role: "Web Developer",
    image: "https://randomuser.me/api/portraits/women/72.jpg",
  },
  {
    text: "Phasellus vestibulum felis sed malesuada consequat. Integer sed mollis nunc. Vivamus mauris ligula, iaculis vel sem sit amet, vehicula elementum nulla.",
    name: "Jane Doe",
    role: "Web Developer",
    image: "https://randomuser.me/api/portraits/women/85.jpg",
  },
];

const HeroSection = () => {
  return (
    <div>
      <div className="responsive-container">
        {/* Left Half */}
        <div className="left-half">
          Industry Leaders <br /> Hire From Us
        </div>
        {/* Right Half */}
        <div className="right-half">
          <p>
            Join our placement-linked programs and become ‘day one job-ready’ professionals
          </p>
          <div className="cta-container">
            <Button variant="primary">Limited Seats, Join Us Today</Button>
          </div>
        </div>
      </div>

      {/* Testimonials Section */}
      <div className="testimonial-section">
        <h2 className="testimonial-title">What Our Students Say</h2>
        <Carousel className="testimonial-carousel">
          {testimonials.map((testimonial, index) => (
            <Carousel.Item key={index}>
              <div className="testimonial-wrapper">
                <p className="testimonial-text">{testimonial.text}</p>
                <div className="testimonial-img-wrapper">
                  <img
                    src={testimonial.image}
                    alt={`testimonial-${index}`}
                    className="testimonial-img"
                  />
                </div>
                <div className="testimonial-person-info">
                  <p>
                    <b>{testimonial.name}</b><br />
                    {testimonial.role}
                  </p>
                </div>
              </div>
            </Carousel.Item>
          ))}
        </Carousel>
      </div>
      {/* Testimonials Ends */}

      {/* Hiring Parters Starts*/}

      {/* Hiring Partners Ends */}
      {/* Footer */}
      {/* Footer is here */}
    </div>
  );
};

export default HeroSection;
