import React, { useState } from 'react';
import './AboutUs.css'; // Include your custom styles

const AboutUs = () => {
  const [activeCategory, setActiveCategory] = useState('All'); // State for active category

  // Example gallery data
  const galleryImages = [
    { id: 1, category: 'Software', src: 'path-to-image1.jpg', alt: 'Software Training' },
    { id: 2, category: 'Placement', src: 'path-to-image2.jpg', alt: 'Placement Event' },
    { id: 3, category: 'Training', src: 'path-to-image3.jpg', alt: 'Employee Training' },
    { id: 4, category: 'Software', src: 'path-to-image4.jpg', alt: 'Software Development' },
    { id: 5, category: 'Employees', src: 'path-to-image5.jpg', alt: 'Employee Team' },
    { id: 6, category: 'Offices', src: 'path-to-image6.jpg', alt: 'Office Workspace' },
  ];

  // Filter images based on the selected category
  const filteredImages = galleryImages.filter((image) => 
    activeCategory === 'All' || image.category === activeCategory
  );

  return (
    <div className="about-us">
      <h1>About Us</h1>

      {/* Pills navigation */}
      <ul className="nav nav-pills mb-4">
        <li className="nav-item">
          <button
            className={`nav-link ${activeCategory === 'All' ? 'active' : ''}`}
            onClick={() => setActiveCategory('All')}
          >
            All
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${activeCategory === 'Software' ? 'active' : ''}`}
            onClick={() => setActiveCategory('Software')}
          >
            Software
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${activeCategory === 'Placement' ? 'active' : ''}`}
            onClick={() => setActiveCategory('Placement')}
          >
            Placement
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${activeCategory === 'Training' ? 'active' : ''}`}
            onClick={() => setActiveCategory('Training')}
          >
            Training
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${activeCategory === 'Employees' ? 'active' : ''}`}
            onClick={() => setActiveCategory('Employees')}
          >
            Employees
          </button>
        </li>
        <li className="nav-item">
          <button
            className={`nav-link ${activeCategory === 'Offices' ? 'active' : ''}`}
            onClick={() => setActiveCategory('Offices')}
          >
            Offices
          </button>
        </li>
      </ul>

      {/* Gallery Section */}
      <div className="gallery">
        {filteredImages.map((image) => (
          <div key={image.id} className="gallery-item">
            <img src={image.src} alt={image.alt} className="img-fluid" />
            <p>{image.alt}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AboutUs;
